mapper.select:
SELECT 
FROM xxl_job_info AS t


		AND t.job_group = #{jobGroup}


		AND t.trigger_status = #{triggerStatus}


		AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')


		AND t.executor_handler like CONCAT(CONCAT('%', #{executorHandler}), '%')


		AND t.author like CONCAT(CONCAT('%', #{author}), '%')


ORDER BY pk_id DESC
LIMIT #{offset}, #{pagesize}

SELECT count(1) FROM xxl_job_info AS t
AND t.job_group = #{jobGroup}
AND t.trigger_status = #{triggerStatus}
AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')
AND t.executor_handler like CONCAT(CONCAT('%', #{executorHandler}), '%')
AND t.author like CONCAT(CONCAT('%', #{author}), '%')

SELECT 
FROM xxl_job_info AS t
WHERE t.pk_id = #{id}

SELECT 
FROM xxl_job_info AS t
WHERE t.job_group = #{jobGroup}

SELECT 
FROM xxl_job_info AS t
WHERE t.trigger_status = 1
and t.trigger_next_time  <=  #{maxNextTime}
ORDER BY pk_id ASC
LIMIT #{pagesize}

mapper.update:
UPDATE xxl_job_info
SET trigger_status = 1
WHERE journey_id = #{manualStartJourneyId}

UPDATE xxl_job_info
SET trigger_status = 2
WHERE journey_id = #{manualStopJourneyId}

mapper.select:
SELECT schedule_conf
FROM xxl_job_info
WHERE journey_id = #{cronJourneyId}

SELECT 
FROM xxl_job_info AS t
WHERE t.journey_id = #{selectJourneyId}
